    const alea2 =  Math.floor((Math.random() * 77) + 1);

    const alea3 =  Math.floor((Math.random() * 77) + 1);

    const alea4 =  Math.floor((Math.random() * 77) + 1);

    const alea5 =  Math.floor((Math.random() * 77) + 1);

    let history = useHistory();

const img = "./animaux/" + alea + ".jpg" ;    
const img2 = "./animaux/" + alea2 + ".jpg" ;    
const img3 = "./animaux/" + alea3 + ".jpg" ;    
const img4 = "./animaux/" + alea4 + ".jpg" ;    
const img5 = "./animaux/" + alea5 + ".jpg" ;    



    return (
     
      <div  className="background">
<div className="navigation"> <button onClick={() => {
                history.push(`/form`);
              } }> Vers autres models </button> </div>
      <div  className="ajustement">


     
      <div className="question grid-container">



      <span value="" > {/* {recupere} */}  {array}
        
        
        
        

             </span>


<div className="haut">
<div className="grid-item">
    
<img
          src={img}
          alt="Carte"
        />

</div></div>

<div className="centre">

  <div className="grid-item">
        <img
          src={img2}
          alt="Carte"
        />

</div>
<div className="grid-item">
        <img
          src={img3}
          alt="Carte"
        />



</div><div className="grid-item">
        <img
          src={img4}
          alt="Carte"
        />
</div>
</div>
<div className="bas">

<div>
        <img
          src={img5}
          alt="Carte"
        />   
       </div></div>
        
       
      
       
      </div></div>
      
      
      
      <div className="navigation2">  <button onClick={() => {
                history.push(`/choix`);
              } }>Choix d'autres cartes</button>  </div></div>
    );
  }
  
  export default Anim;
