var array = pst.replace('%3F', ' ? ').replace('%21', '!').replace('%2C', ',').replace('%C3%', '').replace('%27', "'").replace('%C3%AF', 'ï').replace('%C3%AE', 'î').slice("10" ).split('+').join(' '); 


/*   var recupere = document.getElementsByName('question').value;  */
/* var expressionReguliere = /[?&]?([^=]+)=([^&]*)/g; */

console.log(pst);
function Anim2() {
  

/*   console.log("authState");
  console.log(authState); */
    const alea =  Math.floor((Math.random() * 77) + 1); // Retourne un numéro de carte au hasard

    const alea2 =  Math.floor((Math.random() * 77) + 1);

    const alea3 =  Math.floor((Math.random() * 77) + 1);

    const alea4 =  Math.floor((Math.random() * 77) + 1);

    const alea5 =  Math.floor((Math.random() * 77) + 1);

    let history = useHistory();

const img = "./animaux/" + alea + ".jpg" ;    
const img2 = "./animaux/" + alea2 + ".jpg" ;    
const img3 = "./animaux/" + alea3 + ".jpg" ;    
const img4 = "./animaux/" + alea4 + ".jpg" ;    
const img5 = "./animaux/" + alea5 + ".jpg" ;    



    return (
     
      <div  className="">



              
              <div className="navigation"> <button onClick={() => {
                history.push(`/cartescroix`);
              } }> Poser une autre question </button>  <button onClick={() => {
                history.push(`/form`);
              } }> Vers autres models </button>
              
              
              
              </div>



            



      <div  className="ajustementenligne">
      
  
     <div className="question">
     

      <div className="test1">


<span value="" >  {array} </span>  </div>

  
      <div className="test2">


    
<img
          src={img}
          alt="Carte"
        />





  
        <img
          src={img2}
          alt="Carte"
        />


        <img
          src={img3}
          alt="Carte"
        />




        <img
          src={img4}
          alt="Carte"
        />





        <img
          src={img5}
          alt="Carte"
        />   
      
        
      </div>
      
      </div>
    
      </div>
      
      
     
      
      <div className="navigation2"> <button onClick={() => {
                history.push(`/anim2`);
              } }> Tirage même question </button>  <button onClick={() => {
                history.push(`/choix`);
              } }> Choix d'autres cartes </button>
              
              
              
              </div> </div>
    );
  }
  
  export default Anim2;
