<img
  src={img5}
  alt="Carte"
/>   


</div>

</div>

</div>




<div className="navigation2"> <button onClick={() => {
        history.push(`/tarots2`);
      } }> Tirage même question </button>  <button onClick={() => {
        history.push(`/choix`);
      } }> Choix d'autres cartes </button>
      
      
      
      </div> </div>
