import React, { useState } from "react";
import { useHistory } from "react-router-dom";



class EssayForm extends React.Component {
    constructor(props) {
      super(props);
      this.state = {      value: 'Ecrivez votre question ici'    };
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
      this.myFunction = this.myFunction.bind(this);
      this.history = this.history(this);
    }
   
    myFunction(event) {
       this.history.push("/anim", { state: "question" }); 
      }
      history(event) {
        myFunctionhistory.push("/anim", { state: "question" }); 
       }
 

    handleChange(event) {    this.setState({value: event.target.value});  }
    handleSubmit(event) {
      alert('Un essai a été envoyé : ' + this.state.value);
      event.preventDefault();
    }
  
    render() {
      return (
          
        <form onSubmit={this.myFunction()}>
          <label>
           
            <textarea value={this.state.value} onChange={this.handleChange} row="1" column="30" />   {this.state.value}   </label>
            {console.log(this.state.value)}  
          <input type="submit" value="Envoyer" />
        </form>
        
      );
    }
  }
  export default EssayForm;
 
