  class EssayForm extends React.Component {
    
  
    render() {

        let history = useHistory();
      return (
          
        <span >   <textarea rows="1" cols="80" name="question" id="question" className="question">  </textarea> <button onClick={() => {
            history.push(`/anim`);
          } }>OK</button> 
          
          {/* <EssayForm/> */}
          
          
          
          
           </span>
        
      );
    }
  }
  export default EssayForm;
