/* var array = pst.split("", 3);  */

/* var array = pst.replace('%3F', ' ? ').replace('+', '').slice(21, ).split('+').join(' '); */



var array2 = pst2.replace('%3F', ' ? ').replace('%21', '!').replace('%2C', ',').replace('%27', "'").replace('%C3%AF', 'ï').replace('%C3%AE', 'î').slice("10" ).split('+').join(' ');


/* var expressionReguliere = /[?&]?([^=]+)=([^&]*)/g; */

console.log(pst2);
function Anim2() {
    const alea =  Math.floor((Math.random() * 77) + 1); // Retourne un numéro de carte au hasard

    const alea2 =  Math.floor((Math.random() * 77) + 1);

    const alea3 =  Math.floor((Math.random() * 77) + 1);

    const alea4 =  Math.floor((Math.random() * 77) + 1);

    const alea5 =  Math.floor((Math.random() * 77) + 1);

 

const img = "./animaux/" + alea + ".jpg" ;    
const img2 = "./animaux/" + alea2 + ".jpg" ;    
const img3 = "./animaux/" + alea3 + ".jpg" ;    
const img4 = "./animaux/" + alea4 + ".jpg" ;    
const img5 = "./animaux/" + alea5 + ".jpg" ;    



    return (
     
      <div  className="background">

      <div  className="ajustement2">

<div className="navigationligne"> <button>La page des models</button> <button>Poser une autre question</button>
     
 <button>Même question</button> <button>Vers un didacticiel de cours</button>  </div>



      <div><span >    {array2}  </span></div>
        
<div  className="carteenligne">

    
<img
          src={img}
          alt="Carte"
        />


        <img
          src={img2}
          alt="Carte"
        />



        <img
          src={img3}
          alt="Carte"
        />




        <img
          src={img4}
          alt="Carte"
        />



        <img
          src={img5}
          alt="Carte"
        />   
       
        
       
      
       
     </div> </div>  </div>
    );
  }
  
  export default Anim2;
