import React from "react";
import axios from "axios";
import { useEffect, useState } from "react";
import { Link, useHistory } from "react-router-dom";
import ThumbUpAltIcon from "@material-ui/icons/ThumbUpAlt";
import "bootstrap/dist/css/bootstrap.min.css";
function Home() {
  const [listOfPosts, setListOfPosts] = useState([]);
  const [likedPosts, setLikedPosts] = useState([]);

  let history = useHistory();

  useEffect(() => {
    if (!localStorage.getItem("accessToken")) {
      history.push("/login");
    } else {
      axios
        .get("http://localhost:3001/postspriv", {
          headers: { accessToken: localStorage.getItem("accessToken") },
        })
        .then((response) => {
          setListOfPosts(response.data.listOfPosts);
          setLikedPosts(
            response.data.likedPosts.map((like2) => {
              //Map argument de tableau
              return like2.PostId;
            })
          );
        });
    }
  }, []);

  return (
    <div className="containerpost" id="lessonList">
      {listOfPosts.map((value, key) => {
        //Map argument de tableau
        return (
          /*  <div class="row mb-3">
          <div class="col">
             <input class="form-control" id="searchInput" type="text" placeholder="Search..">
          </div>
       </div> */

          <div key={key} className="post">
            <div className="title"> {value.title} </div>
            <div
              className="space"
              onClick={() => {
                history.push(`/post/${value.id}`);
              }}
            >
              {value.postText}
            </div>

            <div className="lien">
              <iframe
                height="500px"
                width="100%"
                src={value.lien}
                frameBorder="0"
                allow="autoplay; encrypted-media"
                allowFullScreen
              />
              <a target="blank" href={value.lien}>
                {value.lien}
              </a>
            </div>

            <div className="footer">
              <div className="username">
                {" "}
                <Link to={`/profile/${value.UserId}`}>
                  {" "}
                  Créé par <span className="speudo">{value.username} </span> le
                  : {value.createdAt}
                </Link>
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default Home;
