/* var array = pst.replace('%3F', ' ? ').replace('+', '').slice(21, ).split('+').join(' '); */


var array = pst.replace('%3F', ' ? ').replace('%21', '!').replace('%2C', ',').replace('%27', "'").replace('%C3%AF', 'ï').replace('%C3%AE', 'î').slice("5" ).split('+').join(' ');


/* var expressionReguliere = /[?&]?([^=]+)=([^&]*)/g; */

console.log(pst);
function Anim() {
    const alea =  Math.floor((Math.random() * 409) + 1); // Retourne un numéro de carte au hasard

    const alea2 =  Math.floor((Math.random() * 409) + 1);

    const alea3 =  Math.floor((Math.random() * 409) + 1);

    const alea4 =  Math.floor((Math.random() * 409) + 1);

    const alea5 =  Math.floor((Math.random() * 409) + 1);

 

const img = "./animaux/" + alea + ".jpg" ;    
const img2 = "./animaux/" + alea2 + ".jpg" ;    
const img3 = "./animaux/" + alea3 + ".jpg" ;    
const img4 = "./animaux/" + alea4 + ".jpg" ;    
const img5 = "./animaux/" + alea5 + ".jpg" ;    



    return (
     
      <div  className="background">
<div className="navigation"> <button>La page des models</button> <button>Poser une autre question</button>  </div>
      <div  className="ajustement">


     
      <div className="question">



      <span >    {array}  </span>
        
<div className="haut">
<div>
    
<img
          src={img}
          alt="Carte"
        />

</div></div>

<div className="centre">

  <div>
        <img
          src={img2}
          alt="Carte"
        />

</div>
<div>
        <img
          src={img3}
          alt="Carte"
        />



</div><div>
        <img
          src={img4}
          alt="Carte"
        />
</div>
</div>
<div className="bas">

<div>
        <img
          src={img5}
          alt="Carte"
        />   
       </div></div>
        
       
      
       
      </div></div>
      
      
      
      <div className="navigation2"> <button>Même question</button> <button>Vers un didacticiel de cours</button>  </div></div>
    );
  }
  
  export default Anim;
