import React, { useEffect, useState, useContext } from "react";
import { useParams, useHistory } from "react-router-dom";
import axios from "axios";
import { AuthContext } from "../helpers/AuthContext";
import ThumbUpAltIcon from "@material-ui/icons/ThumbUpAlt";
/* import LocalCafeIcon from "@material-ui/icons//LocalCafe"; */

function Profile() {
  let { id } = useParams();
  let history = useHistory();

  const [username, setUsername] = useState("");
  const [photo_profil, setphoto_profil] = useState("");
  const [listOfPosts, setListOfPosts] = useState([]);
  const { authState } = useContext(AuthContext);

  useEffect(() => {
    axios
      .get(`http://localhost:3001/auth/basicinfo/${id}`)
      .then((response) => {
        setUsername(response.data.username);
        setphoto_profil(response.data.photo_profil);

      });

    axios
      .get(`http://localhost:3001/posts/byuserId/${id}`)
      .then((response) => {
        setListOfPosts(response.data);


/* 
        const date =  (response.data.date);
        console.log(date); */
      });


  }, []);




  return (
    <div className="grid">
{/*     <div className="grid" > */}

      <h1> Page de profil public de : {username} </h1>
      <div className="profil">
      <img
          src={"http://localhost:3001/images/" + photo_profil}
          alt="profil"
        />
      </div>
      <div className="boutonpriv">
        <button
          onClick={() => {
            history.push("/postpriv/" + id);
          }}
        >

          Voir la fiche de présentation
        </button>
      </div>
      {/*    <button
        onClick={() => {
          history.push("/createpostpriv");   commentaire
        }}
      >
        {" "}
        créer Publication privées
      </button> */}


        <div className="listepostsparutilisateur">
        {listOfPosts.map((value, key) => {
          return (

            <div key={key} className="post3">
              <div className="title"> {value.title} </div>
              <div
                className="body"
                onClick={() => {
                  history.push(`/post/${value.id}`);
                }}
              >
                {value.postText}
              </div>
            <div className="iframdiv">
                <iframe id="imglien" className="lien"
               scrolling="no"
               frameborder="10"
               overflow="hidden"
               height="100%"
               width="100%" 
                  src={value.lien}

                  allowfullscreen ="true"
                ></iframe>

</div>
         <div className="atarget">
<a target="blank" href={value.lien}>
                  {value.lien}
                </a>
             </div>
              <div className="footer">
                <div className="textfooter">
              {value.createdAt.replace('T', ' à ').slice(0, 18)} 

                  {/*  {value.createdAt} */}

                </div>

                <div className="buttons">
                  <div className="cofee ">
                    {" "}
                    <ThumbUpAltIcon />
                    <label className="labelcof"> {value.Likes.length}</label>
                  </div>
                </div>
              </div>
            </div>  
          );
        })}
      </div></div>

  );
}

export default Profile;
