import React, {} from "react";
import { useHistory } from "react-router-dom";
const pst = window.location.search;  
/* const reg= new RegExp("[ ,;]+", "g","?"); */


/* var array = pst.split("", 3);  */

/* var array = pst.replace('%3F', ' ? ').replace('+', '').slice(21, ).split('+').join(' '); */


var array = pst.replace('%3F', ' ? ').replace('%21', '!').replace('%2C', ',').replace('%27', "'").replace('%C3%AF', 'ï').replace('%C3%AE', 'î').slice("10" ).split('+').join(' ');


/* var expressionReguliere = /[?&]?([^=]+)=([^&]*)/g; */

console.log(pst);
function Tirage() {
    const alea =  Math.floor((Math.random() * 409) + 1); // Retourne un numéro de carte au hasard

    const alea2 =  Math.floor((Math.random() * 409) + 1);

    const alea3 =  Math.floor((Math.random() * 409) + 1);

    const alea4 =  Math.floor((Math.random() * 409) + 1);

    const alea5 =  Math.floor((Math.random() * 409) + 1);

    let history = useHistory();

const img = "./oracle/" + alea + ".jpg" ;    
const img2 = "./oracle/" + alea2 + ".jpg" ;    
const img3 = "./oracle/" + alea3 + ".jpg" ;    
const img4 = "./oracle/" + alea4 + ".jpg" ;    
const img5 = "./oracle/" + alea5 + ".jpg" ;    



    return (
     
      <div  className="background ">
<div className="navigation"> <button onClick={() => {
                history.push(`/cartestiragecroix`);
              } }> Poser une autre question </button>  <button onClick={() => {
                history.push(`/form`);
              } }> Vers autres models </button></div>



      <div  className="ajustement">


     
      <div className="question grid-container">



      <span >    {array}  </span>
        
<div className="haut">
<div className="grid-item">
    
<img
          src={img}
          alt="Carte"
        />

</div></div>

<div className="centre ">

  <div className="grid-item">
        <img
          src={img2}
          alt="Carte"
        />

</div>
<div className="grid-item">
        <img
          src={img3}
          alt="Carte"
        />



</div><div className="grid-item">
        <img
          src={img4}
          alt="Carte"
        />
</div>
</div>
<div className="bas" >

<div className="grid-item" >
        <img
          src={img5}
          alt="Carte"
        />   


       </div></div>
        
       
      
       
      </div></div>
      
      
      
      <div className="navigation2"> <button onClick={() => {
                history.push(`/anim2`);
              } }> Tirage même question </button>  <button onClick={() => {
                history.push(`/choix`);
              } }> Choix d'autres cartes </button>
              
              
              
              </div></div>
    );
  }
  
  export default Tirage;
