import React from "react";
import axios from "axios";
 import "../css/style.css";  
import { useEffect, useState } from "react";
import { Link, useHistory } from "react-router-dom";
import ThumbUpAltIcon from "@material-ui/icons/ThumbUpAlt";
import "bootstrap/dist/css/bootstrap.min.css";






function Home() {
  const [listOfPosts, setListOfPosts] = useState([]);
  const [likedPosts, setLikedPosts] = useState([]);
  const [date, setdate] = useState([]);

  let history = useHistory();

  useEffect(() => {
    if (!localStorage.getItem("accessToken")) {
      history.push("/login");
    } else {
      axios
        .get("http://localhost:3001/posts", {
          headers: { accessToken: localStorage.getItem("accessToken") },
        })
        .then((response) => {
          setListOfPosts(response.data.listOfPosts);
          setDate(response.data.date);


          setLikedPosts(
            response.data.likedPosts.map((like) => {
              //Map argument de tableau
              return like.PostId;
            })
          );
        });
    }
  }, []);



  const likeAPost = (postId) => {
    axios
      .post(
        "http://localhost:3001/likes",
        { PostId: postId },
        { headers: { accessToken: localStorage.getItem("accessToken") } }
      )
      .then((response) => {
        setListOfPosts(
          listOfPosts.map((post) => {
            //Map argument de tableau
            if (post.id === postId) {




              if (response.data.liked) {
                return { ...post, Likes: [...post.Likes, 0] };
              } else {
                const likesArray = post.Likes;
                likesArray.pop();
                return { ...post, Likes: likesArray };
              }
            } else {
              return post;
            }
          })
        );

        if (likedPosts.includes(postId)) {
          setLikedPosts(
            likedPosts.filter((id) => {
              return id !== postId;
            })
          );
        } else {
          setLikedPosts([...likedPosts, postId]);
        }
      });   
  };

  return (
    <div className="containerpost">  



      {listOfPosts.map((value, key) => {


        //Map argument de tableau
        return (


          <div key={key} className="post">
            <div className="title"> {value.title} </div>
            <div
              className="body"
              onClick={() => {
                history.push(`/post/${value.id}`);
              }}
            >
              {value.postText}


            <div className="iframdiv">
            <iframe id="imglien" className="lien"
               scrolling="no"
               FrameBorder="10"
               overflow="hidden"
               height="100%"
               width="100%" 
                  src={value.lien}

                  allowFullScreen="true"
                ></iframe>



              </div>
              <div className="atarget">
              <a target="blank" href={value.lien}>
                {value.lien}
              </a> </div>
            <div>

           </div> </div> 




            <div className="footer">
                <Link to={`/profile/${value.UserId}`}>

                  Créé par <div className="pseudo"> {value.username}   </div>
                  <span className="date">le : {value.createdAt}</span>
                </Link>

                <ThumbUpAltIcon
                  onClick={() => {
                    likeAPost(value.id);
                  }}
                  className={
                    likedPosts.includes(value.id) ? "unlikeBttn" : "likeBttn"
                  }
                />
                <label className="white"> {value.Likes.length}</label>

            </div>  </div> 

        );
      })}
    </div> 
  );
}

export default Home;
