import React, {} from "react";

const pst = window.location.search;  
/* const reg= new RegExp("[ ,;]+", "g","?"); */


/* var array = pst.split("", 3);  */

/* var array = pst.replace('%3F', ' ? ').replace('+', '').slice(21, ).split('+').join(' '); */


var array = pst.replace('%3F', ' ? ').replace('%21', '!').replace('%27', "'").replace('%C3%AF', 'ï').replace('%C3%AE', 'î').slice("7" ).split('+').join(' ');


/* var expressionReguliere = /[?&]?([^=]+)=([^&]*)/g; */

console.log(pst);
function Tirage2() {
    const alea =  Math.floor((Math.random() * 409) + 1); // Retourne un numéro de carte au hasard

    const alea2 =  Math.floor((Math.random() * 409) + 1);

    const alea3 =  Math.floor((Math.random() * 409) + 1);

    const alea4 =  Math.floor((Math.random() * 409) + 1);

    const alea5 =  Math.floor((Math.random() * 409) + 1);

 

const img = "./oracle/" + alea + ".jpg" ;    
const img2 = "./oracle/" + alea2 + ".jpg" ;    
const img3 = "./oracle/" + alea3 + ".jpg" ;    
const img4 = "./oracle/" + alea4 + ".jpg" ;    
const img5 = "./oracle/" + alea5 + ".jpg" ;    



    return (
     
      <div  className="background">

      <div  className="ajustement2">

<div className="navigation2"> <button>La page des models</button> <button>Poser une autre question</button>  </div>
     
      <div className="question2">



      <div><span >    {array}  </span></div>
        
<div className="haut">

    
<img
          src={img}
          alt="Carte"
        />

</div>

<div>
        <img
          src={img2}
          alt="Carte"
        />



        <img
          src={img3}
          alt="Carte"
        />




        <img
          src={img4}
          alt="Carte"
        />

</div>
<div className="bas">
        <img
          src={img5}
          alt="Carte"
        />   
       </div>
        
       
      
       
      </div><div className="navigation2"> <button>Même question</button> <button>Vers un didacticiel de cours</button>  </div></div></div>
    );
  }
  
  export default Tirage2;
